import React from 'react';
import { Container, Typography, Grid, Card, CardContent } from '@mui/material';
import { MedicalServices, SupportAgent, Store, Public, Group } from '@mui/icons-material';

const features = [
  {
    icon: <Store fontSize="large" color="primary" />, title: 'Digital Storefront', desc: 'Easily set up and manage your medical tourism business online.'
  },
  {
    icon: <SupportAgent fontSize="large" color="primary" />, title: 'AI Agents', desc: 'Automate patient coordination and support with intelligent agents.'
  },
  {
    icon: <MedicalServices fontSize="large" color="primary" />, title: 'JCI Hospital Network', desc: 'Seamless coordination with JCI-accredited hospitals worldwide.'
  },
  {
    icon: <Group fontSize="large" color="primary" />, title: 'Lead Generation', desc: 'Automated tools to generate and manage new patient leads.'
  },
  {
    icon: <Public fontSize="large" color="primary" />, title: '24/7 Global Support', desc: 'Round-the-clock assistance for international patients.'
  },
];

const Features = () => (
  <Container maxWidth="lg" sx={{ mt: 8 }}>
    <Typography variant="h3" fontWeight="bold" align="center" gutterBottom>
      Platform Features
    </Typography>
    <Grid container spacing={4} mt={2}>
      {features.map((f, idx) => (
        <Grid item xs={12} sm={6} md={4} key={idx}>
          <Card elevation={3} sx={{ height: '100%' }}>
            <CardContent sx={{ textAlign: 'center' }}>
              {f.icon}
              <Typography variant="h6" fontWeight="bold" mt={2} gutterBottom>
                {f.title}
              </Typography>
              <Typography color="text.secondary">{f.desc}</Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  </Container>
);

export default Features;
