import React from 'react';
import { Container, Typography, Grid, Card, CardContent, Box } from '@mui/material';
import { MedicalServices, SupportAgent, Store, Public, Group, Diversity3, VerifiedUser, Language } from '@mui/icons-material';

const features = [
  {
    icon: <Store fontSize="large" color="primary" />, title: 'Digital Storefront', desc: 'Set up a beautiful online presence for your medical tourism business.'
  },
  {
    icon: <SupportAgent fontSize="large" color="primary" />, title: 'AI Agents', desc: 'Automate patient coordination, support, and lead management.'
  },
  {
    icon: <MedicalServices fontSize="large" color="primary" />, title: 'JCI Hospital Network', desc: 'Coordinate seamlessly with JCI-accredited hospitals worldwide.'
  },
  {
    icon: <Group fontSize="large" color="primary" />, title: 'Lead Generation', desc: 'Advanced tools to attract and manage international patient leads.'
  },
  {
    icon: <Public fontSize="large" color="primary" />, title: '24/7 Global Support', desc: 'Always-on, multilingual support for patients and facilitators.'
  },
  {
    icon: <Diversity3 fontSize="large" color="primary" />, title: 'Traditional Touch', desc: 'Respect for diverse cultures and traditions in patient care.'
  },
  {
    icon: <VerifiedUser fontSize="large" color="primary" />, title: 'Secure & Compliant', desc: 'Data privacy and compliance with international standards.'
  },
  {
    icon: <Language fontSize="large" color="primary" />, title: 'Global Reach', desc: 'Expand your services to patients from every continent.'
  }
];

const Features = () => (
  <Box sx={{ background: 'linear-gradient(90deg, #fff 60%, #e3f2fd 100%)', py: 8 }}>
    <Container maxWidth="lg">
      <Typography variant="h3" fontWeight="bold" align="center" gutterBottom sx={{ fontFamily: 'serif', color: '#1a237e' }}>
        Platform Features
      </Typography>
      <Typography align="center" color="text.secondary" sx={{ mb: 6, fontFamily: 'serif' }}>
        Everything you need to run a modern, global, and culturally respectful medical tourism business.
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {features.map((f, idx) => (
          <Grid item xs={12} sm={6} md={4} key={idx}>
            <Card elevation={3} sx={{ height: '100%', borderRadius: 4 }}>
              <CardContent sx={{ textAlign: 'center', py: 5 }}>
                {f.icon}
                <Typography variant="h6" fontWeight="bold" mt={2} gutterBottom sx={{ fontFamily: 'serif' }}>
                  {f.title}
                </Typography>
                <Typography color="text.secondary">{f.desc}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  </Box>
);

export default Features;
